func TestValidateToken(t *testing.T) {
	mockFedIdentityClient := &MockFedIdentityClient{}
	mockFedIdentityClient.On("GetCredentialsForIdentity", &cognitoidentity.GetCredentialsForIdentityInput{
		IdentityId: aws.String("us-east-1:116cf59a-ef99-41c3-a3f8-b8651db1b5c7"),
		Logins: map[string]*string{
			"cognito-idp.us-west-2.amazonaws.com/us-west-2_8IbrUHXlp": aws.String("eyJraWQiOiJwN1wveVpHa3VibEJQcjhqY3ZcL2Z1NWRNRHlvQ21KWjh6VmQ2bU5XSnVSRFk9IiwiYWxnIjoiUlMyNTYifQ..NyXb6-1R95JIzVZYP_yJL_zYyHhMbqKa_wGPn5uXOPaEAXZqeWjCnGHtpGhRwBl8kZnaXO2EJuWwkZ7bFQgbZL05wBIisd5pO3hRxIb5aHNfU4POQoNF6VrZawbNt3sWm8qZnSkJj_vQkm1MVEntbFqgL6l5MOqcdSRJ31RBAMZozhUgEFqwUoSjBpuq-_KbwYzfoXwxya-jJPUZyYEsgMbrflL_00Gxt9A0o86QlrEN0mTmg0ZFB2ZIhEoOXJ5xoHc9KU1PSoVcwYK3MtjsLS9i-XxXcjoICNf7i1Dq8zb72HfoluquIRsqbd8xRItQG3MJVF_NbQ8EN07Z5OWoMQ"), //nolint:lll
		},
	}).Return(&cognitoidentity.GetCredentialsForIdentityOutput{}, nil)
	gw := &UsersGateway{fedIdentityClient: mockFedIdentityClient}
	res, err := gw.ValidateToken(aws.String("us-east-1:116cf59a-ef99-41c3-a3f8-b8651db1b5c7"), aws.String("eyJraWQiOiJwN1wveVpHa3VibEJQcjhqY3ZcL2Z1NWRNRHlvQ21KWjh6VmQ2bU5XSnVSRFk9IiwiYWxnIjoiUlMyNTYifQ..NyXb6-1R95JIzVZYP_yJL_zYyHhMbqKa_wGPn5uXOPaEAXZqeWjCnGHtpGhRwBl8kZnaXO2EJuWwkZ7bFQgbZL05wBIisd5pO3hRxIb5aHNfU4POQoNF6VrZawbNt3sWm8qZnSkJj_vQkm1MVEntbFqgL6l5MOqcdSRJ31RBAMZozhUgEFqwUoSjBpuq-_KbwYzfoXwxya-jJPUZyYEsgMbrflL_00Gxt9A0o86QlrEN0mTmg0ZFB2ZIhEoOXJ5xoHc9KU1PSoVcwYK3MtjsLS9i-XxXcjoICNf7i1Dq8zb72HfoluquIRsqbd8xRItQG3MJVF_NbQ8EN07Z5OWoMQ")) //nolint:lll
	assert.NoError(t, err)
	assert.NotNil(t, res)
}
